import React, {Component} from 'react';
import './App.css';
import Box from '@material-ui/core/Box';
import Peer from 'peerjs';
import { io } from "socket.io-client";

const servers = {
  iceServers: [
    {
      urls: ['stun:stun1.l.google.com:19302', 'stun:stun2.l.google.com:19302'],
    },
  ],
};

let pc = new RTCPeerConnection(servers);
let localStream = null;
let remoteStream = null;
var peer = new Peer();

const socket = io("http://localhost:9000");



export default class App extends Component {
  constructor(props){
    super(props);
    this.displayVideo = this.displayVideo.bind(this);
    this.callUser = this.callUser.bind(this);
    this.myRef = React.createRef();
    this.remoteRef = React.createRef();
    this.state = {roomId: ""};
  }

  callAPI() {
    fetch("http://localhost:9000/testAPI")
      .then(res => res.text())
      .then(res => this.setState({roomId: res}));
  }


  componentWillMount(){
      this.callAPI();
  }

  callUser(){
    fetch("http://localhost:9000/connect");
    peer.on("call", (call) => {
      call.answer(localStream);
      call.on("stream", (userVideoStream)=> {
        partnerVideo.srcObject = userVideoStream;
      });
    });

    socket.on("user-connected", () => {
      this.connectToUser(localStream);
    });

  }

  connectToUser(stream){
    const call = peer.call(stream);
    call.on("stream", (userVideoStream)=> {
      partnerVideo.srcObject = userVideoStream;
    });
  }

  async displayVideo(){
    remoteStream = new MediaStream();
    const webcamVideo = this.myRef.current;
    localStream = await navigator.mediaDevices.getUserMedia({video: true, audio: true});

    localStream.getTracks().forEach((track) => {
      pc.addTrack(track, localStream);
    });

    pc.ontrack = event => {
      event.streams[0].getTracks().forEach(track => {
          remoteStream.addTrack(track);
      });
  };

    webcamVideo.srcObject = localStream;
  }

  render(){
    return (
      <div className="App">
        <body className = "App-body">
          hi


          <Box className = "hostVideo" p = {2}>
           <video ref = {this.myRef} autoplay = 'true' playsinline></video>
          </Box>
          <button onClick = {this.displayVideo}>Turn on Camera</button>
          <button onClick = {this.callUser}>Call</button>
          <video ref = {this.remoteRef} autoplay = 'true' playsinline></video>

          <p className = "App-intro"></p>
        </body>
       </div>
     );
  }

}
